package com.demo;

public class ReverseInPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int no=1034,count=0;
        while(no>0){
           System.out.print(no%10+" ");
           no=no/10;
           count++;
       }
      // System.out.println("\n"+count);
   }
	}


